"""
About dialogs
"""

import gtk
import hildon
from gettext import gettext as _
from meabook.version import version


class AboutDialog():
    """Hildon About Dialog."""

    def __init__(self):
        pass

    def run(self):
        dialog = hildon.Dialog()
        dialog.set_title(_('About'))
        info_label = gtk.Label()
        info_label.set_use_markup(True)
        info_label.set_justify(gtk.JUSTIFY_CENTER)
        info_label.set_markup("\n<span foreground='white' size='medium'><b>" \
            "Meabook</b></span><span foreground='white' size='small'> - " \
            "%s</span>\n<span foreground='white' size='small'>%s %s</span>\n" \
            "\n<span foreground='white'size='small'><b>%s</b></span>\n<span" \
            " foreground='white' size='small'>%s | </span><span foreground=" \
            "'#299BFC' size='small'>tanyshk@gmail.com</span>\n<span " \
            "foreground='white' size='small'>%s | </span><span foreground=" \
            "'#299BFC' size='small'>maxusachev@gmail.com</span>\n" % \
            (_('Enterprise address book'), _('Version'), version, \
            _('Developers'), _('Tanya Makova'), _('Max Usachev')))
        dialog.vbox.add(info_label)
        dialog.vbox.show_all()
        dialog.run()
        dialog.destroy()
